RSpec.describe "/posts", type: :request do

  # Post. As you add validations to Post, be sure to
  # adjust the attributes here as well.

  subject(:category) { PostCategory.create!(title: 'a' * 5, description: 'a' * 500) }

  let(:user) { User.create!(nickname: 'a' * 5, email: 'user@gmail.com', role: 'user', password: '1' * 6) }
  let(:valid_attributes) do
    {
      author: user,
      category: category,
      category_id: category.id,
      title: 'a' * 50,
      content: 'a' * 200,
      tag_ids: []
    }
  end
  let(:invalid_attributes) do
    { title: 'a' * 2, content: 'a' * 2 }
  end

  describe "GET /index" do
    it "renders a successful response" do
      Post.create! valid_attributes
      get posts_url
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      post = Post.create! valid_attributes
      get post_url(id: post.id)
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      sign_in user
      get new_post_url
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    it "render a successful response" do
      sign_in user
      post = Post.create! valid_attributes
      get edit_post_url(id: post.id)
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new Post" do
        sign_in user
        expect do
          post posts_url, params: { post: valid_attributes }
        end.to change(Post, :count).by(1)
      end

      it "redirects to the created post" do
        sign_in user
        post posts_url, params: { post: valid_attributes }
        expect(response).to redirect_to(post_url(Post.last))
      end
    end

    # context "with invalid parameters" do
    #   it "does not create a new Post" do
    #     expect do
    #       sign_in user
    #       post posts_url, params: { post: invalid_attributes }
    #     end.to change(Post, :count).by(0)
    #   end

    #   it "renders a successful response (i.e. to display the 'new' template)" do
    #     sign_in user
    #     post posts_url, params: { post: invalid_attributes }
    #     expect(response).to be_successful
    #   end
    # end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      let(:new_attributes) do
        { title: 'b' * 50 }
      end

      it "updates the requested post" do
        sign_in user
        post = Post.create! valid_attributes
        patch post_url(id: post.id), params: { post: new_attributes }
        post.reload
        expect(post.title).to eq('b' * 50)
      end

      it "redirects to the post" do
        sign_in user
        post = Post.create! valid_attributes
        patch post_url(id: post.id), params: { post: new_attributes }
        post.reload
        expect(response).to redirect_to(post_url(id: post.id))
      end
    end

    # context "with invalid parameters" do
    #   it "renders a successful response (i.e. to display the 'edit' template)" do
    #     sign_in user
    #     post = Post.create! valid_attributes
    #     patch post_url(post), params: { post: invalid_attributes }
    #     expect(response).to be_successful
    #   end
    # end
  end

  describe "DELETE /destroy" do
    it "destroys the requested post" do
      sign_in user
      post = Post.create! valid_attributes
      expect do
        delete post_url(id: post.id)
      end.to change(Post, :count).by(-1)
    end

    it "redirects to the posts list" do
      sign_in user
      post = Post.create! valid_attributes
      delete post_url(id: post.id)
      expect(response).to redirect_to(posts_url)
    end
  end
end
